import { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import emailjs from '@emailjs/browser';

export default function ReservationApp() {
  const [lang, setLang] = useState('ko');
  const [reservations, setReservations] = useState([]);
  const [form, setForm] = useState({ name: '', email: '', datetimeId: '', workshop: '' });
  const [message, setMessage] = useState('');
  const [adminSlots, setAdminSlots] = useState([]);
  const [newSlot, setNewSlot] = useState({ title: '', date: '', time: '' });
  const [isAdmin, setIsAdmin] = useState(false);
  const [adminAuth, setAdminAuth] = useState(false);
  const [adminPw, setAdminPw] = useState('');
  const [showModal, setShowModal] = useState(false);

  const text = {
    ko: {
      title: adminAuth ? '관리자 슬롯 설정' : '📅 워크숍 예약 시스템',
      name: '이름',
      email: '이메일',
      date: '예약 날짜',
      time: '시간 선택',
      workshop: '워크숍 종류',
      submit: '예약하기',
      reservationTitle: '📋 예약 현황',
      success: name => `✅ ${name}님의 예약이 완료되었습니다!`,
      error: '모든 항목을 입력해주세요.',
      full: '정원이 가득 찼습니다. 다른 시간을 선택해주세요.',
      slotConfig: '슬롯 추가',
      slotList: '📅 예약 가능한 슬롯',
      delete: '삭제',
      login: '관리자 로그인',
      close: '닫기'
    },
    en: {
      title: adminAuth ? 'Admin Slot Management' : '📅 Workshop Reservation System',
      name: 'Name',
      email: 'Email',
      date: 'Reservation Date',
      time: 'Select Time',
      workshop: 'Workshop Type',
      submit: 'Reserve',
      reservationTitle: '📋 Reservation List',
      success: name => `✅ ${name}, your reservation is complete!`,
      error: 'Please fill in all fields.',
      full: 'This time slot is full. Please choose another.',
      slotConfig: 'Add Slot',
      slotList: '📅 Available Reservation Slots',
      delete: 'Delete',
      login: 'Admin Login',
      close: 'Close'
    }
  };

  const t = text[lang];
  const MAX_CAPACITY = 3;

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = () => {
    const { name, email, datetimeId, workshop } = form;
    if (!name || !email || !datetimeId || !workshop) {
      setMessage(<p className="text-red-600">{t.error}</p>);
      return;
    }

    const count = reservations.filter(r => r.datetimeId === datetimeId).length;
    if (count >= MAX_CAPACITY) {
      setMessage(<p className="text-red-600">{t.full}</p>);
      return;
    }

    setReservations([...reservations, form]);
    setShowModal(true);
    setForm({ name: '', email: '', datetimeId: '', workshop: '' });

    emailjs.send("your_service_id", "your_template_id", {
      to_name: name,
      to_email: email,
      datetime: datetimeId,
      workshop: workshop
    }, "your_user_id")
    .then(() => console.log("✅ Email sent"))
    .catch(err => console.error("❌ Email failed", err));
  };

  const uniqueDatetimeIds = [...new Set(adminSlots.map(s => `${s.date} ${s.time}`))];

  const handleSlotAdd = () => {
    if (newSlot.title && newSlot.date && newSlot.time) {
      const id = `${newSlot.date} ${newSlot.time}`;
      const exists = adminSlots.some(slot => slot.date === newSlot.date && slot.time === newSlot.time);
      if (!exists) {
        setAdminSlots([...adminSlots, { ...newSlot, id }]);
      }
      setNewSlot({ title: '', date: '', time: '' });
    }
  };

  const handleSlotDelete = (index) => {
    const updated = [...adminSlots];
    updated.splice(index, 1);
    setAdminSlots(updated);
  };

  const handleAdminLogin = () => {
    if (adminPw === '3931') {
      setAdminAuth(true);
      setAdminPw('');
    } else {
      alert('비밀번호가 틀렸습니다.');
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded shadow">
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-800">{t.title}</h1>
        <div>
          <button onClick={() => setLang(lang === 'ko' ? 'en' : 'ko')} className="text-sm text-blue-600 mr-4">🌐 Toggle</button>
          <button onClick={() => setIsAdmin(!isAdmin)} className="text-sm text-gray-600">{isAdmin ? '👤 사용자 모드' : '🔧 관리자 모드'}</button>
        </div>
      </div>

      {isAdmin ? (
        !adminAuth ? (
          <div className="mt-6">
            <label className="block mb-2">비밀번호
              <input type="password" value={adminPw} onChange={(e) => setAdminPw(e.target.value)} className="w-full mt-1 p-2 border rounded" />
            </label>
            <button onClick={handleAdminLogin} className="bg-blue-600 text-white px-4 py-2 rounded">{t.login}</button>
          </div>
        ) : (
          <>
            <div className="mt-6">
              <label className="block mb-2">워크숍 제목
                <input type="text" value={newSlot.title} onChange={(e) => setNewSlot({ ...newSlot, title: e.target.value })} className="w-full mt-1 p-2 border rounded" />
              </label>
              <label className="block mb-2">날짜
                <input type="date" value={newSlot.date} onChange={(e) => setNewSlot({ ...newSlot, date: e.target.value })} className="w-full mt-1 p-2 border rounded" />
              </label>
              <label className="block mb-4">시간
                <input type="time" value={newSlot.time} onChange={(e) => setNewSlot({ ...newSlot, time: e.target.value })} className="w-full mt-1 p-2 border rounded" />
              </label>
              <button onClick={handleSlotAdd} className="bg-green-600 text-white px-4 py-2 rounded">{t.slotConfig}</button>
            </div>

            <div className="mt-8">
              <h2 className="text-lg font-semibold mb-2">{t.slotList}</h2>
              {adminSlots.map((slot, i) => (
                <div key={i} className="flex justify-between items-center border-b py-1 text-sm">
                  <span>📅 {slot.title} — {slot.date} {slot.time}</span>
                  <button onClick={() => handleSlotDelete(i)} className="text-red-600 text-xs ml-4">{t.delete}</button>
                </div>
              ))}
              <div className="mt-8">
                <h2 className="text-lg font-semibold mb-2">{t.reservationTitle}</h2>
                {reservations.map((r, i) => (
                  <div key={i} className="border-b py-2">
                    <strong>{r.name}</strong> - {r.workshop} ({r.datetimeId})<br />
                    📧 {r.email}
                  </div>
                ))}
              </div>
            </div>
          </>
        )
      ) : (
        <>
          <label className="block mt-4">{t.name}
            <input name="name" value={form.name} onChange={handleChange} className="w-full mt-1 p-2 border rounded" />
          </label>
          <label className="block mt-4">{t.email}
            <input name="email" value={form.email} onChange={handleChange} type="email" className="w-full mt-1 p-2 border rounded" />
          </label>
          <label className="block mt-4">{t.date} + {t.time}
            <select name="datetimeId" value={form.datetimeId} onChange={handleChange} className="w-full mt-1 p-2 border rounded">
              <option value="">--</option>
              {adminSlots.map(slot => {
                const count = reservations.filter(r => r.datetimeId === slot.id).length;
                const disabled = count >= MAX_CAPACITY;
                return (
                  <option key={slot.id} value={slot.id} disabled={disabled}>
                    {slot.title} ({slot.date} {slot.time}) {disabled ? '🔒 마감' : ''}
                  </option>
                );
              })}
            </select>
          </label>
          <label className="block mt-4">{t.workshop}
            <input name="workshop" value={form.workshop} onChange={handleChange} className="w-full mt-1 p-2 border rounded" placeholder="워크숍명 입력" />
          </label>

          <button onClick={handleSubmit} className="mt-6 bg-blue-600 text-white px-4 py-2 rounded">{t.submit}</button>

          {showModal && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white p-6 rounded shadow text-center">
                <p className="text-lg font-semibold">{t.success(form.name || '🎉')}</p>
                <button onClick={() => setShowModal(false)} className="mt-4 bg-gray-600 text-white px-4 py-2 rounded">{t.close}</button>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}
